package dev.barcoder.kr;

public class AssginTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int aVar=20;
		System.out.println("aVar+=10 ->"+(aVar+=10));
		System.out.println("aVar-=10 ->"+(aVar-=10));
		System.out.println("aVar*=10 ->"+(aVar*=10));
		System.out.println("aVar/=10 ->"+(aVar/=10));
		System.out.println("aVar%=10 ->"+(aVar%=10));
	}

}
