package dev.barcoder.kr;

public class IntDataTypeTest {
	
	public static void main(String[] args){
		//TODO Auto-generated method stub
		// 자료형. byte, short, int, long. 각각 처라하는 범위가 다름. 그거 실습하는 것.
		byte aVar = 100;
		short bVar = 30000;
		int cVar = 50000;
		long dVar = 1000000;
		
		System.out.println("byte 타입 : "+ aVar); // sysout. ctrl + spacebar
		System.out.println("short 타입 :"+ bVar);
		System.out.println("int 타입 :"+ cVar);
		System.out.println("long 타입 :"+ dVar);
	}

}
